import React, { useEffect, useState } from 'react';

// ExpenseList now accepts the onEdit and onRefresh props from App.js
export default function ExpenseList({ refresh, onEdit, onRefresh }) { 
  const [expenses, setExpenses] = useState([]);

  const fetchExpenses = async () => {
    try {
      const res = await fetch("http://localhost:5000/expenses");
      const data = await res.json();
      setExpenses(data);
    } catch (error) {
      console.error("Failed to fetch expenses:", error);
    }
  };
  
  // Existing delete function, updated to use the onRefresh prop
  const handleDelete = async (id) => {
    try {
      await fetch(`http://localhost:5000/expenses/${id}`, {
        method: "DELETE",
      });
      // Use the prop function to refresh the list in App.js
      if (onRefresh) onRefresh(); 
    } catch (error) {
      console.error("Failed to delete expense:", error);
    }
  };

  useEffect(() => {
    fetchExpenses();
  }, [refresh]);

  return (
    <div>
      <h2>Expenses</h2>
      <ul>
        {expenses.map((exp) => (
          <li key={exp.id}>
            ${exp.amount} – {exp.category} – {exp.description} – {exp.date.split('T')[0]}
            
            {/* --- NEW: The Edit Button --- */}
            <button 
              onClick={() => onEdit(exp)} // Call the function from App.js, passing the expense data
              style={{ marginLeft: '10px', background: 'blue', color: 'white' }}>
              Edit
            </button>
            {/* --- Existing Delete Button --- */}
            <button 
              onClick={() => handleDelete(exp.id)} 
              style={{ marginLeft: '10px', background: 'red', color: 'white' }}>
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}
