import React, { useEffect, useState } from 'react';

export default function ExpenseList({ refresh }) {
  const [expenses, setExpenses] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5000/expenses")
      .then((res) => res.json())
      .then((data) => setExpenses(data));
  }, [refresh]);

  return (
    <div>
      <h2>Expenses</h2>
      <ul>
        {expenses.map((exp) => (
          <li key={exp.id}>
            ${exp.amount} – {exp.category} – {exp.description} – {exp.date}
          </li>
        ))}
      </ul>
    </div>
  );


// frontend/src/ExpenseList.js - Add the handleDelete function and a button

// ... inside the ExpenseList component ...

  const handleDelete = async (id) => {
    await fetch(`http://localhost:5000/expenses/${id}`, {
      method: "DELETE",
    });
    // After deletion, refresh the list to show the change
    fetchExpenses(); // Re-run the fetch function
  };
  
  // Update the useEffect to call an internal fetch function
  useEffect(() => {
      fetchExpenses();
  }, [refresh]);

// ... update the render part ...

  return (
    <div>
      <h2>Expenses</h2>
      <ul>
        {expenses.map((exp) => (
          <li key={exp.id}>
            ${exp.amount} – {exp.category} – {exp.description} – {exp.date}
            <button onClick={() => handleDelete(exp.id)} style={{ marginLeft: '10px' }}>
                Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}
