import React, { useState } from "react";
import ExpenseForm from "./components/ExpenseForm";
import ExpenseList from "./components/ExpenseList";
import EditExpenseForm from "./components/EditExpenseForm"; // 1. NEW IMPORT

function App() {
  const [refresh, setRefresh] = useState(false);
  // 2. NEW STATE: Stores the expense object to edit, or null if adding a new one.
  const [editingExpense, setEditingExpense] = useState(null); 

  const handleRefresh = () => {
    // Toggles state to force ExpenseList to re-fetch data
    setRefresh(!refresh);
    // 3. Exit editing mode after a save/successful update
    setEditingExpense(null); 
  };

  const handleEdit = (expenseToEdit) => {
    // 4. Function passed to ExpenseList to start the edit process
    setEditingExpense(expenseToEdit);
  };

  const handleCancelEdit = () => {
    // 5. Function passed to EditExpenseForm to exit editing mode
    setEditingExpense(null);
  };

  // Conditional Rendering: Show Edit Form or Add Form
  const renderForm = () => {
    if (editingExpense) {
      // If editingExpense is set, show the Edit form
      return (
        <EditExpenseForm 
          expense={editingExpense} 
          onSave={handleRefresh} // Save triggers refresh and exits edit mode
          onCancel={handleCancelEdit} // Cancel just exits edit mode
        />
      );
    } else {
      // Otherwise, show the standard Add form
      return <ExpenseForm onAdd={handleRefresh} />;
    }
  };

  return (
    <div>
      <h1>Expense Tracker</h1>

      {/* 6. Render the correct form based on state */}
      {renderForm()}

      {/* 7. Pass the new handleEdit function to ExpenseList */}
      <ExpenseList 
        refresh={refresh} 
        onEdit={handleEdit} 
        onRefresh={handleRefresh} // Pass delete refresh function down
      />
    </div>
  );
}

export default App;
